"use client";
import type React from "react";
import { useState, useCallback } from "react";
import { LexicalComposer } from "@lexical/react/LexicalComposer";
import { RichTextPlugin } from "@lexical/react/LexicalRichTextPlugin";
import { ContentEditable } from "@lexical/react/LexicalContentEditable";
import { HistoryPlugin } from "@lexical/react/LexicalHistoryPlugin";
import { AutoFocusPlugin } from "@lexical/react/LexicalAutoFocusPlugin";
import { LinkPlugin } from "@lexical/react/LexicalLinkPlugin";
import { ListPlugin } from "@lexical/react/LexicalListPlugin";
import { CheckListPlugin } from "@lexical/react/LexicalCheckListPlugin";
import { TablePlugin } from "@lexical/react/LexicalTablePlugin";
import TableHoverActionsPlugin from "./plugins/table-hover-actions";
import { HorizontalRulePlugin } from "@lexical/react/LexicalHorizontalRulePlugin";
import { MarkdownShortcutPlugin } from "@lexical/react/LexicalMarkdownShortcutPlugin";
import { LexicalErrorBoundary } from "@lexical/react/LexicalErrorBoundary";
import { OnChangePlugin } from "@lexical/react/LexicalOnChangePlugin";
import SlashCommandPlugin from "./plugins/slash-command";
import { TRANSFORMERS } from "@lexical/markdown";
import { EDITOR_CONFIG } from "./lib/configs";
import { Toolbar } from "./plugins/toolbar";
import { FloatingToolbar } from "./plugins/floating-toolbar";
import type { EditorProps } from "./lib/types/editor";
import { type EditorState, type LexicalEditor } from "lexical";

function EditorContent({
  placeholder = "Start writing ...",
  className = "",
  minHeight = "400px",
  maxHeight,
  readOnly = false,
}: Pick<
  EditorProps,
  "placeholder" | "className" | "minHeight" | "maxHeight" | "readOnly"
>) {
  const editorStyle = {
    minHeight,
    maxHeight,
    caretColor: "hsl(var(--editor-primary))",
    lineHeight: "1.7",
  };

  return (
    <div className="relative">
      <RichTextPlugin
        contentEditable={
          <ContentEditable
            className={`
              p-6 md:p-8 
              outline-none 
              max-w-none 
              transition-all duration-300
              ${className}
            `}
            style={editorStyle}
            readOnly={readOnly}
          />
        }
        placeholder={
          <div className="absolute top-6 md:top-8 left-6 md:left-8 text-muted-foreground/60 pointer-events-none select-none text-base md:text-lg">
            {placeholder}
          </div>
        }
        ErrorBoundary={LexicalErrorBoundary}
      />
    </div>
  );
}

function EditorPlugins({
  showFloatingToolbar = true,
  customPlugins = [],
  onChange = () => {},
}: {
  showFloatingToolbar?: boolean;
  customPlugins?: React.ComponentType[];
  onChange: (
    editorState: EditorState,
    editor: LexicalEditor,
    tags: Set<string>
  ) => void;
}) {
  return (
    <>
      <HistoryPlugin />
      <AutoFocusPlugin />
      <ListPlugin />
      <CheckListPlugin />
      <LinkPlugin />

      <HorizontalRulePlugin />
      {/* Table plugins - order matters! */}
      <TablePlugin
        hasCellMerge={true}
        hasCellBackgroundColor={true}
        hasTabHandler={true}
      />
      <TableHoverActionsPlugin />
      <SlashCommandPlugin />
      <MarkdownShortcutPlugin transformers={TRANSFORMERS} />
      <OnChangePlugin onChange={onChange} />
      {showFloatingToolbar && <FloatingToolbar />}
      {customPlugins.map((Plugin, index) => (
        <Plugin key={index} />
      ))}
    </>
  );
}

export function Editor({
  initialValue = "",
  placeholder = 'Start writing or use "/" for quick commands',
  className = "",
  minHeight = "400px",
  maxHeight,
  showToolbar = false,
  showFloatingToolbar = true,
  readOnly = false,
  onChange,
  plugins = [],
}: EditorProps) {
  const [, setEditorState] = useState<string>(initialValue);

  const initialConfig = {
    ...EDITOR_CONFIG,
    editorState: initialValue ? initialValue : null,
    editable: !readOnly,
  };

  const handleEditorChange = useCallback(
    (editorState: EditorState) => {
      const jsonState = editorState.toJSON();
      const jsonString = JSON.stringify(jsonState);

      setEditorState(jsonString);
      onChange?.(jsonString);
    },
    [onChange]
  );

  return (
    <div className={`w-full ${className}`}>
      <LexicalComposer initialConfig={initialConfig}>
        <div className=" relative overflow-hidden">
          {showToolbar && <Toolbar />}

          <EditorContent
            placeholder={placeholder}
            minHeight={minHeight}
            maxHeight={maxHeight}
            readOnly={readOnly}
          />

          <EditorPlugins
            showFloatingToolbar={showFloatingToolbar}
            customPlugins={plugins}
            onChange={handleEditorChange}
          />
        </div>
      </LexicalComposer>
    </div>
  );
}

import type React from "react";
import { useState, useRef } from "react";
import { Upload } from "lucide-react";

import { Button } from "@/registry/new-york/ui/button";
import { Input } from "@/registry/new-york/ui/input";
import { Label } from "@/registry/new-york/ui/label";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/registry/new-york/ui/dialog";

export function ImageDialog({
  isOpen,
  onClose,
  onSubmit,
}: {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (src: string, alt: string) => void;
}) {
  const [url, setUrl] = useState("");
  const [alt, setAlt] = useState("");
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (url.trim()) {
      onSubmit(url.trim(), alt.trim() || "Image");
    }
    onClose();
    setUrl("");
    setAlt("");
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        if (result) {
          onSubmit(result, file.name);
          onClose();
          setUrl("");
          setAlt("");
        }
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <>
      {isOpen && (
        <Dialog open={isOpen} onOpenChange={onClose}>
          <DialogContent
            aria-describedby="image-dialog"
            className="sm:max-w-md backdrop-blur-md bg-background/95"
          >
            <div>
              <DialogHeader>
                <DialogTitle className="text-xl font-semibold">
                  Insert Image
                </DialogTitle>
              </DialogHeader>
              <div className="space-y-4 mt-4">
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <Label htmlFor="image-url" className="text-sm font-medium">
                      Image URL
                    </Label>
                    <Input
                      id="image-url"
                      value={url}
                      onChange={(e) => setUrl(e.target.value)}
                      placeholder="https://example.com/image.jpg"
                      className="mt-1.5"
                    />
                  </div>
                  <div>
                    <Label htmlFor="image-alt" className="text-sm font-medium">
                      Alt Text (optional)
                    </Label>
                    <Input
                      id="image-alt"
                      value={alt}
                      onChange={(e) => setAlt(e.target.value)}
                      placeholder="Describe the image"
                      className="mt-1.5"
                    />
                  </div>
                  <div className="flex gap-2 justify-end">
                    <Button type="button" variant="outline" onClick={onClose}>
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      disabled={!url.trim()}
                      className="bg-primary hover:bg-primary/90"
                    >
                      Insert Image
                    </Button>
                  </div>
                </form>

                <div className="relative">
                  <div className="absolute inset-0 flex items-center">
                    <span className="w-full border-t border-border/60" />
                  </div>
                  <div className="relative flex justify-center text-xs uppercase">
                    <span className="bg-background px-2 text-muted-foreground font-medium">
                      Or
                    </span>
                  </div>
                </div>

                <div>
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileChange}
                    accept="image/*"
                    className="hidden"
                  />
                  <Button
                    type="button"
                    variant="outline"
                    className="w-full hover:bg-accent/80 transition-colors bg-transparent"
                    onClick={() => fileInputRef.current?.click()}
                  >
                    <Upload className="size-4 mr-2" />
                    Upload from Computer
                  </Button>
                </div>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </>
  );
}

export * from "./link-dialog";
export * from "./table-dialog";
export * from "./image-dialog";

import type React from "react";
import { useState, useEffect } from "react";

import { Button } from "@/registry/new-york/ui/button";
import { Input } from "@/registry/new-york/ui/input";
import { Label } from "@/registry/new-york/ui/label";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/registry/new-york/ui/dialog";

export function LinkDialog({
  isOpen,
  onClose,
  onSubmit,
  initialUrl = "",
}: {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (url: string) => void;
  initialUrl?: string;
}) {
  const [url, setUrl] = useState(initialUrl);

  useEffect(() => {
    if (isOpen) {
      setUrl(initialUrl);
    }
  }, [isOpen, initialUrl]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (url.trim()) {
      onSubmit(url.trim());
    }
    onClose();
  };

  return (
    <>
      {isOpen && (
        <Dialog open={isOpen} onOpenChange={onClose}>
          <DialogContent
            aria-describedby="link-dialog"
            className="sm:max-w-md backdrop-blur-md bg-background/95"
          >
            <div>
              <DialogHeader>
                <DialogTitle className="text-xl font-semibold">
                  Insert Link
                </DialogTitle>
              </DialogHeader>
              <form onSubmit={handleSubmit} className="space-y-4 mt-4">
                <div>
                  <Label htmlFor="url" className="text-sm font-medium">
                    URL
                  </Label>
                  <Input
                    id="url"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="https://example.com"
                    autoFocus
                    className="mt-1.5 focus:ring-2 focus:ring-primary/20 transition-all"
                  />
                </div>
                <div className="flex gap-2 justify-end">
                  <Button type="button" variant="outline" onClick={onClose}>
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="bg-primary hover:bg-primary/90"
                  >
                    Insert Link
                  </Button>
                </div>
              </form>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </>
  );
}

import type React from "react";
import { useState } from "react";
import { Button } from "@/registry/new-york/ui/button";
import { Input } from "@/registry/new-york/ui/input";
import { Label } from "@/registry/new-york/ui/label";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/registry/new-york/ui/dialog";

export function TableDialog({
  isOpen,
  onClose,
  onSubmit,
}: {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (rows: number, columns: number) => void;
}) {
  const [rows, setRows] = useState(3);
  const [columns, setColumns] = useState(3);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(rows, columns);
    onClose();
  };

  return (
    <>
      {isOpen && (
        <Dialog open={isOpen} onOpenChange={onClose}>
          <DialogContent
            aria-describedby="table-dialog"
            className="sm:max-w-md backdrop-blur-md bg-background/95"
          >
            <div>
              <DialogHeader>
                <DialogTitle className="text-xl font-semibold">
                  Insert Table
                </DialogTitle>
              </DialogHeader>
              <form onSubmit={handleSubmit} className="space-y-4 mt-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="rows" className="text-sm font-medium">
                      Rows
                    </Label>
                    <Input
                      id="rows"
                      type="number"
                      min="1"
                      max="20"
                      value={rows}
                      onChange={(e) =>
                        setRows(
                          Math.max(1, Number.parseInt(e.target.value) || 1)
                        )
                      }
                      className="mt-1.5"
                    />
                  </div>
                  <div>
                    <Label htmlFor="columns" className="text-sm font-medium">
                      Columns
                    </Label>
                    <Input
                      id="columns"
                      type="number"
                      min="1"
                      max="20"
                      value={columns}
                      onChange={(e) =>
                        setColumns(
                          Math.max(1, Number.parseInt(e.target.value) || 1)
                        )
                      }
                      className="mt-1.5"
                    />
                  </div>
                </div>
                <div className="flex gap-2 justify-end">
                  <Button type="button" variant="outline" onClick={onClose}>
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="bg-primary hover:bg-primary/90"
                  >
                    Insert Table
                  </Button>
                </div>
              </form>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </>
  );
}

import { Moon, Sun } from "lucide-react";
import { useTheme } from "next-themes";

import { Button } from "@/registry/new-york/ui/button";
import { Menu, MenuPopup, MenuItem, MenuTrigger } from "@/registry/new-york/ui/menu";

export function ModeToggle() {
  const { setTheme } = useTheme();

  return (
    <Menu>
      <MenuTrigger render={<Button variant="ghost" size="sm" />}>
        <Sun className="h-[1.2rem] w-[1.2rem] scale-100 rotate-0 transition-all dark:scale-0 dark:-rotate-90" />
        <Moon className="absolute h-[1.2rem] w-[1.2rem] scale-0 rotate-90 transition-all dark:scale-100 dark:rotate-0" />
        <span className="sr-only">Toggle theme</span>
      </MenuTrigger>
      <MenuPopup align="end">
        <MenuItem onClick={() => setTheme("light")}>Light</MenuItem>
        <MenuItem onClick={() => setTheme("dark")}>Dark</MenuItem>
        <MenuItem onClick={() => setTheme("system")}>System</MenuItem>
      </MenuPopup>
    </Menu>
  );
}

import { cn } from "@/registry/new-york/libs/utils";

