export default Separator;

import { nodes } from "./nodes";
import EditorTheme from "./editor-theme";

function onError(error: Error) {
  console.error("Lexical error:", error);
}

export const EDITOR_CONFIG = {
  namespace: "Editor",
  theme: EditorTheme,
  onError,
  nodes,
};

import { useState, useCallback, useEffect, useRef } from "react";
import { useLexicalComposerContext } from "@lexical/react/LexicalComposerContext";
import {
  $getSelection,
  $isRangeSelection,
  SELECTION_CHANGE_COMMAND,
  COMMAND_PRIORITY_LOW,
} from "lexical";
import { mergeRegister } from "@lexical/utils";

interface Position {
  top: number;
  left: number;
  opacity: number;
}

interface UseFloatingToolbarReturn {
  toolbarRef: React.RefObject<HTMLDivElement | null>;
  isVisible: boolean;
  position: Position;
  activeFormats: Set<string>;
  selectedText: string;
}

export function useFloatingToolbar(): UseFloatingToolbarReturn {
  const [editor] = useLexicalComposerContext();
  const toolbarRef = useRef<HTMLDivElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout>(null);

  const [activeFormats, setActiveFormats] = useState<Set<string>>(new Set());
  const [isVisible, setIsVisible] = useState(false);
  const [selectedText, setSelectedText] = useState("");
  const [position, setPosition] = useState<Position>({
    top: -1000,
    left: -1000,
    opacity: 0,
  });

  const calculatePosition = useCallback((rect: DOMRect): Position => {
    const toolbar = toolbarRef.current;
    if (!toolbar) return { top: -1000, left: -1000, opacity: 0 };

    const toolbarRect = toolbar.getBoundingClientRect();
    const viewport = {
      width: window.innerWidth,
      height: window.innerHeight,
    };

    let top = rect.top + window.scrollY - toolbarRect.height - 10;
    let left =
      rect.left + window.scrollX + rect.width / 2 - toolbarRect.width / 2;

    if (left < 10) {
      left = 10;
    } else if (left + toolbarRect.width > viewport.width - 10) {
      left = viewport.width - toolbarRect.width - 10;
    }

    if (top < window.scrollY + 10) {
      top = rect.bottom + window.scrollY + 10;
    }

    return { top, left, opacity: 1 };
  }, []);

  const updateToolbar = useCallback(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    editor.read(() => {
      const selection = $getSelection();

      if (
        $isRangeSelection(selection) &&
        !selection.isCollapsed() &&
        selection.getTextContent().trim() !== ""
      ) {
        const nativeSelection = window.getSelection();
        if (!nativeSelection || nativeSelection.rangeCount === 0) {
          setIsVisible(false);
          return;
        }

        const domRange = nativeSelection.getRangeAt(0);
        const rect = domRange.getBoundingClientRect();
        const text = selection.getTextContent().trim();

        if (text.length < 2) {
          setIsVisible(false);
          return;
        }

        setSelectedText(text);

        const formats = new Set<string>();
        if (selection.hasFormat("bold")) formats.add("bold");
        if (selection.hasFormat("italic")) formats.add("italic");
        if (selection.hasFormat("underline")) formats.add("underline");
        if (selection.hasFormat("code")) formats.add("code");
        if (selection.hasFormat("strikethrough")) formats.add("strikethrough");
        if (selection.hasFormat("superscript")) formats.add("superscript");
        if (selection.hasFormat("subscript")) formats.add("subscript");

        setActiveFormats(formats);

        setIsVisible(true);

        requestAnimationFrame(() => {
          const newPosition = calculatePosition(rect);
          setPosition(newPosition);
        });
      } else {
        timeoutRef.current = setTimeout(() => {
          setIsVisible(false);
          setPosition((prev) => ({ ...prev, opacity: 0 }));
        }, 100);
      }
    });
  }, [editor, calculatePosition]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as Node;
      const editorRoot = editor.getRootElement();

      if (
        isVisible &&
        toolbarRef.current &&
        !toolbarRef.current.contains(target) &&
        !editorRoot?.contains(target)
      ) {
        setIsVisible(false);
      }
    };

    const handleScroll = () => {
      if (isVisible) {
        updateToolbar();
      }
    };

    const handleResize = () => {
      if (isVisible) {
        const nativeSelection = window.getSelection();
        if (nativeSelection && nativeSelection.rangeCount > 0) {
          const domRange = nativeSelection.getRangeAt(0);
          const rect = domRange.getBoundingClientRect();
          const newPosition = calculatePosition(rect);
          setPosition(newPosition);
        }
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    window.addEventListener("scroll", handleScroll, { passive: true });
    window.addEventListener("resize", handleResize, { passive: true });

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
      window.removeEventListener("resize", handleResize);
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [editor, isVisible, updateToolbar, calculatePosition]);

  useEffect(() => {
    return mergeRegister(
      editor.registerUpdateListener(({ editorState }) => {
        editorState.read(updateToolbar);
      }),
      editor.registerCommand(
        SELECTION_CHANGE_COMMAND,
        () => {
          updateToolbar();
          return false;
        },
        COMMAND_PRIORITY_LOW
      )
    );
  }, [editor, updateToolbar]);

  return {
    toolbarRef,
    isVisible,
    position,
    activeFormats,
    selectedText,
  };
}

import type { JSX } from "react";
import { DecoratorNode } from "lexical";
import Image from "next/image";

import type { ImageNodeSerialized } from "../types/editor";

export class ImageNode extends DecoratorNode<JSX.Element> {
  __src: string;
  __alt: string;

  static getType(): string {
    return "image";
  }

  static clone(node: ImageNode): ImageNode {
    return new ImageNode(node.__src, node.__alt, node.__key);
  }

  static importJSON(serializedNode: ImageNodeSerialized): ImageNode {
    const { src, alt } = serializedNode;
    return new ImageNode(src, alt);
  }

  exportJSON(): ImageNodeSerialized {
    return {
      type: "image",
      version: 1,
      src: this.__src,
      alt: this.__alt,
    } as ImageNodeSerialized;
  }

  constructor(src: string, alt = "Image", key?: string) {
    super(key);
    this.__src = src;
    this.__alt = alt;
  }

  createDOM(): HTMLElement {
    const div = document.createElement("div");
    div.className = "image-node";
    return div;
  }

  updateDOM(): boolean {
    return false;
  }

  decorate(): JSX.Element {
    return (
      <Image
        src={this.__src || "/placeholder.svg"}
        alt={this.__alt}
        width={500}
        height={500}
        // onError={() => setIsLoadError(true)}
      />
    );
  }
}

export function $createImageNode(src: string, alt?: string): ImageNode {
  return new ImageNode(src, alt);
}

import { HeadingNode, QuoteNode } from "@lexical/rich-text";
import { ListItemNode, ListNode } from "@lexical/list";
import { CodeHighlightNode, CodeNode } from "@lexical/code";
import { LinkNode, AutoLinkNode } from "@lexical/link";
import { HorizontalRuleNode } from "@lexical/react/LexicalHorizontalRuleNode";
import { TableCellNode, TableNode, TableRowNode } from "@lexical/table";
import { ImageNode } from "./image-node";

export const nodes = [
  HeadingNode,
  ListNode,
  ListItemNode,
  QuoteNode,
  CodeNode,
  CodeHighlightNode,
  LinkNode,
  AutoLinkNode,
  HorizontalRuleNode,
  TableNode,
  TableCellNode,
  TableRowNode,
  ImageNode,
];

import type { SerializedLexicalNode } from "lexical";

export type ImageNodeSerialized = {
  src: string;
  alt: string;
  type: string;
  version: 1;
} & SerializedLexicalNode;

export interface EditorProps {
  initialValue?: string;
  placeholder?: string;
  className?: string;
  minHeight?: string;
  maxHeight?: string;
  showToolbar?: boolean;
  showFloatingToolbar?: boolean;
  readOnly?: boolean;
  autoFocus?: boolean;
  onChange?: (value: string) => void;
  onBlur?: () => void;
  onFocus?: () => void;
  plugins?: React.ComponentType[];
  theme?: Record<string, string>;
}

import type { LexicalEditor } from "lexical";
import { $generateHtmlFromNodes } from "@lexical/html";
import { $convertToMarkdownString } from "@lexical/markdown";
import { TRANSFORMERS } from "@lexical/markdown";
import { $getRoot } from "lexical";

function download(filename: string, text: string) {
  const element = document.createElement("a");
  element.setAttribute(
    "href",
    "data:text/plain;charset=utf-8," + encodeURIComponent(text)
  );
  element.setAttribute("download", filename);

  element.style.display = "none";
  document.body.appendChild(element);

  element.click();

  document.body.removeChild(element);
}

export function exportAsHTML(editor: LexicalEditor) {
  editor.update(() => {
    const htmlString = $generateHtmlFromNodes(editor, null);
    download("editor-content.html", htmlString);
  });
}

export function exportAsMarkdown(editor: LexicalEditor) {
  editor.update(() => {
    const markdown = $convertToMarkdownString(TRANSFORMERS);
    download("editor-content.md", markdown);
  });
}

export function copyAsPlainText(editor: LexicalEditor) {
  editor.getEditorState().read(() => {
    const text = $getRoot().getTextContent();
    navigator.clipboard.writeText(text);
  });
}

import type { LexicalEditor } from "lexical";
import { $convertFromMarkdownString } from "@lexical/markdown";
import { TRANSFORMERS } from "@lexical/markdown";

export function importMarkdown(editor: LexicalEditor, file: File) {
  const reader = new FileReader();
  reader.onload = (e) => {
    const text = e.target?.result as string;
    if (text) {
      editor.update(() => {
        $convertFromMarkdownString(text, TRANSFORMERS);
      });
    }
  };
  reader.readAsText(file);
}

