export function FloatingToolbar() {
  const [editor] = useLexicalComposerContext();
  const { toolbarRef, isVisible, position, activeFormats, selectedText } =
    useFloatingToolbar();

  const formatText = useCallback(
    (format: string) => {
      editor.dispatchCommand(FORMAT_TEXT_COMMAND, format as TextFormatType);
      editor.focus();
    },
    [editor]
  );

  const formatHighlight = useCallback(
    (color: string) => {
      editor.update(() => {
        const selection = $getSelection();
        if ($isRangeSelection(selection)) {
          $patchStyleText(selection, {
            "background-color": color || "transparent",
          });
        }
      });
      editor.focus();
    },
    [editor]
  );

  const groupedItems = useMemo(() => {
    const groups = FORMAT_ITEMS.reduce((acc, item) => {
      if (!acc[item.group]) acc[item.group] = [];
      acc[item.group].push(item);
      return acc;
    }, {} as Record<string, FormatItem[]>);

    return groups;
  }, []);

  if (!isVisible) return null;

  return createPortal(
    <div
      ref={toolbarRef}
      className="absolute z-50 flex items-center gap-1 p-2 bg-popover/95 backdrop-blur-md border border-border/50 rounded-lg shadow-lg transition-all duration-200 ease-out"
      style={{
        top: `${position.top}px`,
        left: `${position.left}px`,
        opacity: position.opacity,
        transform: position.opacity === 1 ? "scale(1)" : "scale(0.95)",
      }}
      onMouseDown={(e) => e.preventDefault()}
    >
      {groupedItems.basic?.map((item) => (
        <ToolbarToggleButton
          key={item.name}
          onClick={() => formatText(item.format)}
          isActive={activeFormats.has(item.format)}
          icon={item.icon}
          title={item.name}
        />
      ))}

      <Separator />

      {groupedItems.special?.map((item) => (
        <ToolbarToggleButton
          key={item.name}
          onClick={() => formatText(item.format)}
          isActive={activeFormats.has(item.format)}
          icon={item.icon}
          title={item.name}
        />
      ))}

      <Separator />

      {groupedItems.script?.map((item) => (
        <ToolbarToggleButton
          key={item.name}
          onClick={() => formatText(item.format)}
          isActive={activeFormats.has(item.format)}
          icon={item.icon}
          title={item.name}
        />
      ))}

      <Separator />

      <Menu>
        <MenuTrigger
          render={<ToolbarButton title="Highlight" icon={Highlighter} />}
        />
        <MenuPopup
          className="w-48 animate-in slide-in-from-top-2 duration-200"
          align="center"
        >
          <div className="grid grid-cols-3 gap-1 p-2">
            {HIGHLIGHT_COLORS.map((color) => (
              <button
                key={color.value}
                onClick={() => formatHighlight(color.value)}
                className="flex flex-col items-center gap-1 p-2 rounded hover:bg-muted/50 transition-colors"
                title={color.name}
              >
                <div
                  className="w-6 h-4 rounded border border-border/50 shadow-sm"
                  style={{ backgroundColor: color.value }}
                />
                <span className="text-xs text-muted-foreground">
                  {color.name}
                </span>
              </button>
            ))}
          </div>
          <MenuSeparator />
          <MenuItem
            onClick={() => formatHighlight("")}
            className="flex items-center gap-2"
          >
            <Palette className="size-4" />
            Remove Highlight
          </MenuItem>
        </MenuPopup>
      </Menu>

      {selectedText.length > 50 && (
        <>
          <Separator />
          <div className="text-xs text-muted-foreground px-2">
            {selectedText.length} chars
          </div>
        </>
      )}
    </div>,
    document.body
  );
}

import { useLexicalComposerContext } from "@lexical/react/LexicalComposerContext";
import {
  LexicalTypeaheadMenuPlugin,
  useBasicTypeaheadTriggerMatch,
  MenuOption,
} from "@lexical/react/LexicalTypeaheadMenuPlugin";
import { TextNode } from "lexical";
import { useCallback, useMemo, useState } from "react";
import { SLASH_COMMANDS, type SlashCommand } from "./slash-command-items";
import { createPortal } from "react-dom";
import { SlashCommandMenuItem } from "./slash-command-menu-item";

class SlashCommandOption extends MenuOption {
  command: SlashCommand;

  constructor(command: SlashCommand) {
    super(command.title);
    this.command = command;
  }
}

export default function SlashCommandPlugin() {
  const [editor] = useLexicalComposerContext();
  const [query, setQuery] = useState<string | null>(null);

  const triggerFn = useBasicTypeaheadTriggerMatch("/", { minLength: 0 });

  const options = useMemo(() => {
    const filteredCommands = query
      ? SLASH_COMMANDS.filter(
          (cmd) =>
            cmd.title.toLowerCase().includes(query.toLowerCase()) ||
            cmd.keywords.some((keyword) =>
              keyword.toLowerCase().includes(query.toLowerCase())
            )
        )
      : SLASH_COMMANDS;

    return filteredCommands.map((cmd) => new SlashCommandOption(cmd));
  }, [query]);

  const onSelectOption = useCallback(
    (
      option: SlashCommandOption,
      nodeToRemove: TextNode | null,
      closeMenu: () => void
    ) => {
      editor.update(() => {
        nodeToRemove?.remove();
        option.command.action(editor);
        closeMenu();
      });
    },
    [editor]
  );

  return (
    <LexicalTypeaheadMenuPlugin
      onQueryChange={setQuery}
      onSelectOption={onSelectOption}
      triggerFn={triggerFn}
      options={options}
      menuRenderFn={(
        anchorElementRef,
        { selectedIndex, selectOptionAndCleanUp, setHighlightedIndex }
      ) => {
        const anchorElement = anchorElementRef.current;

        if (!anchorElement || options.length === 0) {
          return null;
        }

        const rect = anchorElement.getBoundingClientRect();

        return createPortal(
          <div
            className="mt-1 absolute z-50 w-44 max-h-(--available-height) min-w-32 origin-(--transform-origin) overflow-x-hidden overflow-y-auto rounded-md border bg-popover p-1 shadow-md"
            style={{
              top: rect.bottom + window.scrollY + 1,
              left: rect.left + window.scrollX,
            }}
            onMouseDown={(e) => e.preventDefault()}
          >
            {options.map((option, i) => (
              <SlashCommandMenuItem
                isSelected={selectedIndex === i}
                onClick={() => {
                  setHighlightedIndex(i);
                  selectOptionAndCleanUp(option);
                }}
                onMouseEnter={() => {
                  setHighlightedIndex(i);
                }}
                key={option.key}
                command={option.command}
              />
            ))}
          </div>,
          document.body
        );
      }}
    />
  );
}

