export function BlockFormatDropDown({ blockType }: { blockType: string }) {
  const [editor] = useLexicalComposerContext();

  const formatHeading = (headingSize: "h1" | "h2" | "h3" | "h4") => {
    if (blockType !== headingSize) {
      editor.update(() => {
        const selection = $getSelection();
        if ($isRangeSelection(selection)) {
          $setBlocksType(selection, () => $createHeadingNode(headingSize));
        }
      });
    }
  };

  const formatParagraph = () => {
    if (blockType !== "paragraph") {
      editor.update(() => {
        const selection = $getSelection();
        if ($isRangeSelection(selection)) {
          $setBlocksType(selection, () => $createParagraphNode());
        }
      });
    }
  };

  return (
    <Menu>
      <MenuTrigger
        render={
          <ToolbarButton
            variant={
              ["h1", "h2", "h3"].includes(blockType) ? "secondary" : "ghost"
            }
          />
        }
        className="lowercase"
      >
        {blockType === "paragraph" ? (
          <Heading className="size-4" />
        ) : blockType === "h1" ? (
          <Heading1 className="size-4" />
        ) : blockType === "h2" ? (
          <Heading2 className="size-4" />
        ) : blockType === "h3" ? (
          <Heading3 className="size-4" />
        ) : blockType === "h4" ? (
          <Heading4 className="size-4" />
        ) : (
          <Heading className="size-4" />
        )}
      </MenuTrigger>
      <MenuPopup align="start">
        {[
          {
            key: "heading 1",
            icon: Heading1,
            format: "h1",
            state: blockType === "h1",
            onClick: () => formatHeading("h1"),
          },
          {
            key: "heading 2",
            icon: Heading2,
            format: "h2",
            state: blockType === "h2",
            onClick: () => formatHeading("h2"),
          },
          {
            key: "heading 3",
            icon: Heading3,
            format: "h3",
            state: blockType === "h3",
            onClick: () => formatHeading("h3"),
          },
          {
            key: "heading 4",
            icon: Heading4,
            format: "h4",
            state: blockType === "h4",
            onClick: () => formatHeading("h4"),
          },
          {
            key: "paragraph",
            icon: Heading,
            format: "p",
            state: blockType === "paragraph",
            onClick: formatParagraph,
          },
        ].map(({ key, icon: Icon, state, onClick }) => (
          <MenuItem
            key={key}
            className={"flex items-center justify-between gap-5"}
            onClick={onClick}
          >
            <div className="flex items-center">
              <Icon className="size-4" />{" "}
              <span className="ml-2 capitalize text-sm text-muted-foreground">
                {key}
              </span>
            </div>
            {state ? <Check className="ml-auto size-4" /> : null}
          </MenuItem>
        ))}
      </MenuPopup>
    </Menu>
  );
}

import { useLexicalComposerContext } from "@lexical/react/LexicalComposerContext";
import { $createCodeNode } from "@lexical/code";
import { $createQuoteNode } from "@lexical/rich-text";
import { $setBlocksType } from "@lexical/selection";
import {
  $createParagraphNode,
  $getSelection,
  $isRangeSelection,
} from "lexical";
import { BLOCK_FORMAT_ITEMS } from "../toolbar-items";
import { ToolbarToggleButton } from "./toolbar-button";
import { ToolbarState } from "..";

const BLOCK_TYPE_ITEMS = BLOCK_FORMAT_ITEMS.filter((item) => item.format);

export function BlockTypeButtons({
  toolbarState,
}: {
  toolbarState: ToolbarState;
}) {
  const [editor] = useLexicalComposerContext();

  const onClickHandler = (format: string) => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        if (toolbarState.blockType === format) {
          $setBlocksType(selection, () => $createParagraphNode());
        } else {
          if (format === "quote") {
            $setBlocksType(selection, () => $createQuoteNode());
          } else if (format === "code") {
            $setBlocksType(selection, () => $createCodeNode());
          }
        }
      }
    });
  };

  return (
    <>
      {BLOCK_TYPE_ITEMS.map((item) => {
        return (
          <ToolbarToggleButton
            key={item.name}
            onClick={() => onClickHandler(item.format!)}
            isActive={toolbarState.blockType === item.name}
            icon={item.icon}
            title={item.name}
          />
        );
      })}
    </>
  );
}

import { useState, useCallback, useEffect } from "react";
import {
  $getSelection,
  $isRangeSelection,
  SELECTION_CHANGE_COMMAND,
  COMMAND_PRIORITY_CRITICAL,
  type LexicalEditor,
} from "lexical";
import {
  $getSelectionStyleValueForProperty,
  $patchStyleText,
} from "@lexical/selection";
import { Palette } from "lucide-react";

import { Menu, MenuPopup, MenuItem, MenuTrigger } from "@/registry/new-york/ui/menu";
import { FONT_COLORS } from "../../../lib/colors";
import { ToolbarButton } from "@/components/blocks/editor/plugins/toolbar/extensions/toolbar-button";

export function ColorPicker({
  editor,
  disabled = false,
}: {
  editor: LexicalEditor;
  disabled?: boolean;
}) {
  const [color, setColor] = useState("hsl(var(--foreground))");

  const applyColor = useCallback(
    (newColor: string) => {
      editor.update(() => {
        const selection = $getSelection();
        if ($isRangeSelection(selection)) {
          $patchStyleText(selection, { color: newColor });
        }
      });
    },
    [editor]
  );

  useEffect(() => {
    return editor.registerCommand(
      SELECTION_CHANGE_COMMAND,
      () => {
        editor.read(() => {
          const selection = $getSelection();
          if ($isRangeSelection(selection)) {
            setColor(
              $getSelectionStyleValueForProperty(
                selection,
                "color",
                "hsl(var(--foreground))"
              )
            );
          }
        });
        return false;
      },
      COMMAND_PRIORITY_CRITICAL
    );
  }, [editor]);

  return (
    <Menu>
      <MenuTrigger render={<ToolbarButton disabled={disabled} />}>
        <Palette className="size-4" style={{ color }} />
      </MenuTrigger>
      <MenuPopup className="animate-in grid grid-cols-3 gap-1 slide-in-from-top-2 duration-200">
        {FONT_COLORS.map((c) => (
          <MenuItem
            key={c.name}
            className=""
            onClick={() => applyColor(c.value)}
          >
            <div
              className="size-4 rounded-sm border border-input/20 shadow-sm"
              style={{ backgroundColor: c.value }}
            />
            {c.name}
          </MenuItem>
        ))}
      </MenuPopup>
    </Menu>
  );
}

