import { ToolbarState } from "..";
import { LexicalCommand } from "lexical";

export function TextFormatButtons({
  toolbarState,
}: {
  toolbarState: ToolbarState;
}) {
  const [editor] = useLexicalComposerContext();

  const handleClick = (command: LexicalCommand<string>, payload?: string) => {
    if (!payload) return;
    editor.dispatchCommand(command, payload);
  };

  return (
    <>
      {TEXT_FORMAT_ITEMS.map((item) => (
        <ToolbarToggleButton
          key={item.name}
          onClick={() => handleClick(item.command, item.payload)}
          isActive={
            !!toolbarState[
              `is${
                item.name.charAt(0).toUpperCase() + item.name.slice(1)
              }` as keyof ToolbarState
            ]
          }
          icon={item.icon}
          title={item.name}
        />
      ))}
    </>
  );
}

import * as React from "react";
import { Button, buttonVariants } from "@/registry/new-york/ui/button";
import { Toggle } from "@/registry/new-york/ui/toggle";
import type { LucideIcon } from "lucide-react";
import type { VariantProps } from "class-variance-authority";
import { cn } from "@/registry/new-york/libs/utils";

interface ToggleProps {
  onClick: () => void;
  isActive: boolean;
  icon: LucideIcon;
  title: string;
}

export function ToolbarToggleButton({
  onClick,
  isActive,
  icon: Icon,
  title,
}: ToggleProps) {
  return (
    <Toggle
      size="sm"
      pressed={isActive}
      onPressedChange={onClick}
      title={title}
      onMouseDown={(e) => e.preventDefault()}
    >
      <Icon className="size-4" />
    </Toggle>
  );
}

interface ToolbarButtonProps
  extends React.ComponentProps<typeof Button>,
    Partial<VariantProps<typeof buttonVariants>> {
  isActive?: boolean;
  icon?: LucideIcon;
  children?: React.ReactNode;
}

export const ToolbarButton = React.forwardRef<
  HTMLButtonElement,
  ToolbarButtonProps
>(
  (
    {
      className,
      isActive = false,
      children,
      icon: Icon,
      variant,
      size = "icon-sm",
      ...props
    },
    ref,
  ) => {
    return (
      <Button
        ref={ref}
        variant={variant ?? (isActive ? "secondary" : "ghost")}
        size={size}
        className={cn("h-7 w-7 px-[calc(--spacing(1.5)-1px)]", className)}
        {...props}
      >
        {children}
        {Icon && <Icon className="size-4" />}
      </Button>
    );
  },
);

ToolbarButton.displayName = "ToolbarButton";
